import React, { Component } from "react";
import { Form, FormInput, FormGroup, Container, Button } from "shards-react";
import "./signupPage.css";
import { Redirect } from "react-router-dom";

class SignupCreds extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fields: {},

      errormsg: "",
      cansignup: false,

      errors: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.validateForm = this.validateForm.bind(this);
  }

  handleChange(event) {
    let fields = this.state.fields;
    fields[event.target.name] = event.target.value;
    this.setState({
      fields
    });
  }

  validateForm() {
    let fields = this.state.fields;
    let errors = {};
    // errors["password"] = "";
    // errors["secondpass"] = "";
    let formIsValid = true;

    if (typeof fields["password"] !== "undefined") {
      if (
        !fields["password"].match(
          /^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/
        )
      ) {
        formIsValid = false;
        errors["password"] = "Please enter secure and strong password.";

        // At least 8 chars
        // Contains at least one digit
        // Contains at least one lower alpha char and one upper alpha char
        // Contains at least one char within a set of special chars (@#%$^ etc.)
      }

      if (!fields["secondpass"]) {
        formIsValid = false;
        errors["secondpass"] = "Please re-enter your password.";
      }

      if (typeof fields["secondpass"] !== "undefined") {
        if (!fields["secondpass"].match(fields["password"])) {
          formIsValid = false;
          errors["secondpass"] = "Password does not match.";
        }
      }

      this.setState({
        errors: errors
      });

      this.setState({
        errormsg: errors
      });

      // if (
      //   this.state.errors.password !== "" &&
      //   this.state.errors.secondpass == ""
      // ) {
      //   this.setState({ errormsg: this.state.errors.password });
      // }
      // if (
      //   this.state.errors.secondpass !== "" &&
      //   this.state.errors.password == ""
      // ) {
      //   this.setState({
      //     errormsg: this.state.errors.secondpass
      //   });
      // }
      // if (
      //   this.state.errors.secondpass !== "" &&
      //   this.state.errors.password !== ""
      // ) {
      //   this.setState({
      //     errormsg: this.state.errors.password + this.state.errors.secondpass
      //   });
      // }

      return formIsValid;
    }
  }

  handleSubmit(event) {
    event.preventDefault();
    if (this.validateForm()) {
      let fields = {};
      fields["username"] = "";
      fields["email"] = "";
      fields["password"] = "";
      fields["secondpass"] = "";
      this.setState({ fields: fields });

      // username: esc
      // password: hard
      var unirest = require("unirest");
      var req = unirest(
        "POST",
        "https://ug-api.acnapiv3.io/swivel/acnapi-common-services/common/users"
      );

      req.headers({
        "cache-control": "no-cache",
        "content-type": "application/json",
        "server-token":
          "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6IlF6Y3hRVEl5UkRVeU1qYzNSakEzTnpKQ01qVTROVVJFUlVZelF6VTRPRUV6T0RreE1UVTVPQSJ9..z1jKmmePUGifu9NYNIfYQTKl4hAGEPlY4B7QXKSjn2mFgvIz6ncLGxqg4EMf9DmtDKYciA4ssUEeY4wRqJZL66XMJtRznhOoVUkZzEmVMK_5WuS8eoM59BFxO-vaqfgs00heNW9jiJmhl4DuVc5uBY-QTvqzBz-7ahdNgSE47dEje0djpoDfFAXo7F211HXGTC8YfR2zwf3eyhFVAyvRvNrMfDbUGnzlRESL-Qm9kWS55svGC6gAnz_0LURNDrM_ctqbiPGVrUBCajkzSOW33YF5oTJsnD5lqwaET5dqogyT6To5jp9e7Nu5Kf6aTNPRy4KGsVF2dFDr0lvExTj0Qg"
      });

      req.type("json");
      req.send({
        username: this.state.fields.username,
        password: this.state.fields.password,
        email: this.state.fields.email
      });

      req.end(res => {
        console.log(res);
        console.log(res.body);
        //if no errors detected from api
        if (res.error == false) {
          this.setState({ canlogin: false });
        } else {
          this.setState({ errormsg: res.body.error });
        }
      });
    }
    console.log(
      "New signup: \n" +
        "username: " +
        this.state.fields.username +
        "\nemail: " +
        this.state.fields.email +
        "\npassword: " +
        this.state.fields.password +
        "\nre-entered password:" +
        this.state.fields.secondpass
    );
  }

  // handleSubmit(event) {
  //   if (this.validateForm()) {
  //     let fields = {};
  //     fields["username"] = "";
  //     fields["email"] = "";
  //     fields["password"] = "";
  //     fields["secondpass"] = "";
  //     this.setState({ fields: fields });
  //     alert("Form submitted");
  //   }
  //   console.log(
  //     "New signup: \n" +
  //       "username: " +
  //       this.state.username +
  //       "\nemail: " +
  //       this.state.email +
  //       "\npassword: " +
  //       this.state.password +
  //       "\nre-entered password:" +
  //       this.state.secondpass
  //   );
  //   // username: esc
  //   // password: hard
  //   var unirest = require("unirest");
  //   var req = unirest(
  //     "POST",
  //     "https://ug-api.acnapiv3.io/swivel/acnapi-common-services/common/users"
  //   );

  //   req.headers({
  //     "cache-control": "no-cache",
  //     "content-type": "application/json",
  //     "server-token":
  //       "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6IlF6Y3hRVEl5UkRVeU1qYzNSakEzTnpKQ01qVTROVVJFUlVZelF6VTRPRUV6T0RreE1UVTVPQSJ9..z1jKmmePUGifu9NYNIfYQTKl4hAGEPlY4B7QXKSjn2mFgvIz6ncLGxqg4EMf9DmtDKYciA4ssUEeY4wRqJZL66XMJtRznhOoVUkZzEmVMK_5WuS8eoM59BFxO-vaqfgs00heNW9jiJmhl4DuVc5uBY-QTvqzBz-7ahdNgSE47dEje0djpoDfFAXo7F211HXGTC8YfR2zwf3eyhFVAyvRvNrMfDbUGnzlRESL-Qm9kWS55svGC6gAnz_0LURNDrM_ctqbiPGVrUBCajkzSOW33YF5oTJsnD5lqwaET5dqogyT6To5jp9e7Nu5Kf6aTNPRy4KGsVF2dFDr0lvExTj0Qg"
  //   });

  //   req.type("json");
  //   req.send({
  //     username: this.state.username,
  //     password: this.state.password,
  //     email: this.state.email
  //   });

  //   req.end(function(res) {
  //     console.log(res);
  //     console.log(res.body);
  //   });

  //   event.preventDefault();
  // }

  render() {
    if (this.state.cansignup) {
      return <Redirect push to="/login" />;
    }
    return (
      <div class="w-50 mx-auto">
        <h1 className="title">Signup</h1>
        <Form>
          <FormGroup>
            <label>Username</label>
            <FormInput
              placeholder="Enter a username"
              name="username"
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Email Address</label>
            <FormInput
              type="email"
              placeholder="Enter your email"
              name="email"
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Password</label>
            <FormInput
              type="password"
              placeholder="Enter a password"
              name="password"
              onChange={this.handleChange}
            />

            <FormInput
              className="secondpass"
              name="secondpass"
              type="password"
              placeholder="Re-enter password"
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label className="errormsg">{this.state.errormsg}</label>
          </FormGroup>
        </Form>
        <Button onClick={this.handleSubmit}>Register</Button>
      </div>
    );
  }
}

export default SignupCreds;
