    super(props);

    this.state = {
      statusColor_1: "danger",
      statusWords_1: "Unsolved",
      takeWords_1: "Take it!",

      statusColor_2: "danger",
      statusWords_2: "Unsolved",
      takeWords_2: "Take it!",

      statusColor_3: "danger",
      statusWords_3: "Unsolved",
      takeWords_3: "Take it!",

