                </Col>
                <Col sm="12" md="12" lg="1" />
                <Col sm="12" md="12" lg="2" className="TakeItSection">
                  <Button
                    theme="dark"
                    className="TakeItButt"
                    onClick={this.changeStatus_1}
                  >
                    {this.state.takeWords_1}
                  </Button>
                </Col>
