import React, { Component } from "react";
import { Form, FormInput, FormGroup, Container, Button } from "shards-react";
import "./signupPage.css";

class SignupCreds extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      email: "",
      password: "",
      secondpass: "",

      errors: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.validateForm = this.validateForm.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({ [name]: value }, () => {
      this.validateField(name, value);
    });
  }

  // handlePassword(event) {
  //   this.setState({ secondpass: event.target.secondpass });

  //   if (this.state.secondpass == this.state.password) {
  //     this.setState({ valid: true });
  //   }

  //   if (this.state.valid) {
  //     console.log("OKAY");
  //   } else {
  //     console.log("NOT OKAY");
  //   }
  // }

  validateForm() {
    this.setState({
      formValid: this.state.emailValid && this.state.passValid
    });
  }

  handleSubmit(event) {
    console.log(
      "New signup: \n" +
        "username: " +
        this.state.username +
        "\nemail: " +
        this.state.email +
        "\npassword: " +
        this.state.password +
        "\nre-entered password:" +
        this.state.secondpass
    );
    // username: esc
    // password: hard
    var unirest = require("unirest");
    var req = unirest(
      "POST",
      "https://ug-api.acnapiv3.io/swivel/acnapi-common-services/common/users"
    );

    req.headers({
      "cache-control": "no-cache",
      "content-type": "application/json",
      "server-token":
        "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6IlF6Y3hRVEl5UkRVeU1qYzNSakEzTnpKQ01qVTROVVJFUlVZelF6VTRPRUV6T0RreE1UVTVPQSJ9..z1jKmmePUGifu9NYNIfYQTKl4hAGEPlY4B7QXKSjn2mFgvIz6ncLGxqg4EMf9DmtDKYciA4ssUEeY4wRqJZL66XMJtRznhOoVUkZzEmVMK_5WuS8eoM59BFxO-vaqfgs00heNW9jiJmhl4DuVc5uBY-QTvqzBz-7ahdNgSE47dEje0djpoDfFAXo7F211HXGTC8YfR2zwf3eyhFVAyvRvNrMfDbUGnzlRESL-Qm9kWS55svGC6gAnz_0LURNDrM_ctqbiPGVrUBCajkzSOW33YF5oTJsnD5lqwaET5dqogyT6To5jp9e7Nu5Kf6aTNPRy4KGsVF2dFDr0lvExTj0Qg"
    });

    req.type("json");
    req.send({
      username: this.state.username,
      password: this.state.password,
      email: this.state.email
    });

    req.end(function(res) {
      if (res.error) throw new Error(res.error);

      console.log(res.body);
    });

    event.preventDefault();
  }

  render() {
    return (
      <div class="w-50 mx-auto">
        <h1 className="title">Signup</h1>
        <Form>
          <FormGroup>
            <label>Username</label>
            <FormInput
              placeholder="Enter a username"
              name="username"
              onChange={this.handleChange}
            />
            {/* <label>{this.state.emailValid}</label> */}
          </FormGroup>
          <FormGroup>
            <label>Email Address</label>
            <FormInput
              type="email"
              placeholder="Enter your email"
              name="email"
              onChange={this.handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label>Password</label>
            <FormInput
              type="password"
              placeholder="Enter a password"
              name="password"
              onChange={this.handleChange}
            />
            <FormInput
              className="secondpass"
              name="secondpass"
              type="password"
              placeholder="Re-enter password"
              onChange={this.handlePassword}
            />
          </FormGroup>
        </Form>
        <Button onClick={this.handleSubmit} disabled={this.state.valid}>
          Register
        </Button>
      </div>
    );
  }
}

export default SignupCreds;
